        COLORGROUP=variable | COLORRESPONSE=numeric-variable </option(s)>;
     HIGHLOW X=variable | Y=variable HIGH=numeric-variable LOW=numeric-variable</option(s)>; 
     HISTOGRAM response-variable </option(s)>; 
     HLINE category-variable </option(s)>; 
     INSET variable <…variable-n> </option(s)>; 
     KEYLEGEND <"name-1" … "name-n"> </option(s)>; 
     LINEPARM X=numeric-value | numeric-variableY=numeric-value | numeric-variable 
        SLOPE=numeric-value | numeric-variable</option(s)>; 
