statements.help:
PROC BTL <DATA=SAS-data-set> <MAP=SAS-data-set> <NOMCMPR> <NOPARMPR> <NOPRINT> <NOREGPR> <ORDER=DATA | FORMATTED | FREQ... >;     
	BY <DESCENDING> <NOTSORTED> ;

	CLASS <TRUNCATE> ;

	MARKER <ALL=number> <ALLUPTO=number> <BEST=number> ...;

	MODEL <NOINT> ;

	PARMEST <ALPHA=number> <BOOT=number> <CROSS=<BACK|B|DH | INTER|F>> ...;

	PARMS <HOLD=value-list | EQCONS=value-list> <LOWERB=value-list> <NOBOUND> ...;

	RANDOM <GDATA=SAS-data-set> <GROUP=effect> <LDATA=SAS-data-set> ...;

	REPEATED <GROUP=effect> <LDATA=SAS-data-set> <LOCAL=<EXP | POM>> ...;

	WEIGHT variable ;
