PROC CASUTIL   &lt;<i>options</i>&gt;;
	ALTERTABLE  CASDATA="<i>table-name</i>" &lt;INCASLIB="<i>caslib</i>"&gt; &lt;LABEL="<i>label</i>"&gt;
		&lt;RENAME | CASOUT="<i>table-name</i>"&gt;
		&lt;COLUMNS={<i>column-options-1</i> &lt;,{<i>column-options-n</i>}&gt;}&gt;
		&lt;COLUMNORDER={"<i>variable-name</i>" &lt;,"<i>variable-name-n</i>"&gt;}&gt;
		&lt;DROP={"<i>variable-name</i>" &lt;,"<i>variable-name-n</i>"&gt;}&gt;
		&lt;KEEP={"<i>variable-name</i>" &lt;,"<i>variable-name-n</i>"&gt;}&gt;;
	CONTENTS  CASDATA="<i>table-name</i>" &lt;INCASLIB="<i>caslib</i>"&gt; &lt;<i>options</i>&gt;;
	 DELETESOURCE  CASDATA="<i>file-name</i>"  &lt;INCASLIB="<i>caslib</i>"&gt; &lt;DATASOURCEOPTIONS={<i>data-source-options</i>}&gt; &lt;DELETEOPTIONS={<i>delete-options</i>}&gt; &lt;QUIET&gt;;
	COPY   CASDATA="<i>file-name</i>" &lt;INCASLIB="<i>caslib</i>"&gt;
		CASOUT="<i>table-name</i>"&lt;OUTCASLIB="<i>caslib</i>"&gt;

		 &lt;DATASOURCEOPTIONS=(<i>data-source-options</i>)&gt;
			&lt;INDEXVARS=((<i>variable-name-1</i>),&lt;(<i>variable-name-n</i>)&gt;)&gt;&lt;<i>options</i>&gt;;
	DROPTABLE  CASDATA="<i>table-name</i>"  &lt;INCASLIB="<i>caslib</i>"&gt; &lt;QUIET&gt;;
	INDEX  CASDATA="<i>table-name</i>" 
		&lt;INCASLIB="<i>caslib</i>"&gt;
		&lt;VARS={ {<i>casinvardesc-1</i>}, {&lt;<i>casinvardesc-n</i>&gt;} }&gt;
		&lt;WHERE="<i>where-expression</i> &lt;<i>logical-operator</i><i>where-expression</i>"&gt;&gt;
		&lt;IMPORTOPTIONS=(FILETYPE=<i>file-type</i> &lt;<i>file-type-options</i>&gt;)&gt;
		&lt;DATASOURCEOPTIONS=(<i>data-source-options</i>)&gt;
		&lt;CASOUT="<i>table-name</i>"&gt;
		&lt;OUTCASLIB="<i>caslib</i>"&gt;
		&lt;LABEL="<i>label-name</i>"&gt;
		&lt;COMPRESS &gt;&lt;PROMOTE &gt;&lt;REPLACE &gt;&lt;REPLICATION|COPIES= <i>integer</i>&gt;
		&lt;INDEXVARS=((<i>variable-name-1</i>),&lt;(<i>variable-name-n</i>)&gt;)&gt;;
	LIST  &lt;FILES | TABLES&gt;  &lt;<i>options</i>&gt;;
	LOAD  CASDATA="<i>file-name</i>" | DATA=<i>SAS-data-set</i> | FILE="<i>SAS-file</i>" &lt;<i>options</i>&gt;;
	PARTITION  CASDATA="<i>table-name</i>" 
		&lt;INCASLIB="<i>caslib</i>"&gt;
		&lt;VARS={ {<i>casinvardesc-1</i>}, {&lt;<i>casinvardesc-n</i>&gt;} }&gt;
		&lt;WHERE="<i>where-expression</i> &lt;<i>logical-operator</i><i>where-expression</i>"&gt;&gt;
		&lt;PARTITIONBY | GROUPBY=(<i>group-by-variable-1</i> &lt;<i>group-by-variable-n</i>&gt;)&gt;
		&lt;IMPORTOPTIONS=(FILETYPE=<i>file-type</i> &lt;<i>file-type-options</i>&gt;)&gt;
		&lt;DATASOURCEOPTIONS=(<i>data-source-options</i>)&gt;
		&lt;CASOUT="<i>table-name</i>"&gt;
		&lt;OUTCASLIB="<i>caslib</i>"&gt;
		&lt;LABEL="<i>label-name</i>"&gt;
		&lt;COMPRESS &gt;&lt;PROMOTE &gt;&lt;REPLACE &gt;&lt;REPLICATION|COPIES= <i>integer</i>&gt;
		&lt;INDEXVARS=((<i>variable-name-1</i>),&lt;(<i>variable-name-n</i>)&gt;)&gt;
	PROMOTE   CASDATA="<i>table-name</i>" &lt;INCASLIB="<i>caslib</i>"&gt; 
		&lt;CASOUT="<i>table-name</i>"&gt; 
		&lt;OUTCASLIB="<i>caslib</i>"&gt;
		  &lt;DROP | KEEP&gt;; 
	SAVE   CASDATA="<i>table-name </i>"  &lt;INCASLIB="<i>caslib</i>"&gt;
		&lt;CASOUT="<i>file-name</i>"&gt; &lt;OUTCASLIB="<i>caslib</i>"&gt; &lt;EXPORTOPTIONS={<i>export-options</i>}&gt; &lt;IMPORTDATASOURCEOPTIONS = {<i>import-data-source-options</i>}&gt;
		  &lt;<i>options</i>&gt;;
	UPDATE  CASDATA="<i>table-name</i>" 
		&lt;INCASLIB="<i>caslib</i>"&gt;
		&lt;SET={ {var="<i>variable-name</i>", value="<i>string</i>"} }&gt;
		&lt;DATASOURCEOPTIONS=(<i>data-source-options</i>)&gt;
		&lt;IMPORTOPTIONS=(FILETYPE=<i>file-type</i> &lt;<i>file-type-options</i>&gt;)&gt;
		&lt;GROUPBY=(<i>group-by-variable-1</i>&lt;<i>group-by-variable-n</i>&gt;)&gt;
		&lt;ORDERBY=(<i>variable-1</i>&lt;<i>variable-n</i>&gt;)&gt;
		&lt;WHERE="<i>where-expression</i> &lt;<i>logical-operator</i><i>where-expression</i>"&gt;;&gt;QUIT;

Form 1: 
LOAD  CASDATA="<i>file-name</i>" &lt;INCASLIB="<i>caslib</i>"&gt; CASOUT="<i>table-name</i>"
	&lt;OUTCASLIB="<i>caslib</i>"&gt; 
		&lt;IMPORTOPTIONS=(FILETYPE="<i>file-type</i>" &lt;<i>file-type-options</i>&gt;)&gt;
		&lt;GROUPBY=(<i>group-by-variable-1</i> &lt;<i>group-by-variable-2</i> ...&gt;)&gt; &lt;ORDERBY=(<i>variable-1</i>
		 &lt;<i>variable-2</i> ...&gt;)&gt;
		&lt;LABEL="<i>table-description</i>"&gt;
		&lt;DATASOURCEOPTIONS=(<i>data-source-options</i>)&gt;
		&lt;PROMOTE | REPLACE&gt;
		 &lt;COPIES=<i>integer</i>&gt;
		&lt;VARS=((<i>casinvardesc-1</i>) &lt;, (<i>casinvardesc-2</i>),  ...&gt;)&gt;
		&lt;WHERE="<i>where-expression-1</i> &lt;<i>logical-operator</i><i>where-expression-2</i>&gt;"&gt;
		;
Form 2: 
LOAD  DATA=<i>SAS-data-set</i>&lt;(<i>data-set-options</i>)&gt; 

	&lt;CASOUT="<i>table-name</i>"&gt; 
		&lt;OUTCASLIB="<i>caslib</i>"&gt; 
		&lt;APPEND | COMPRESS&gt;
		&lt;GROUPBY=(<i>group-by-variable-1</i> &lt;<i>group-by-variable-2</i> ...&gt;) &gt; &lt;ORDERBY=(<i>variable-1</i>
		 &lt;<i>variable-2</i> ...&gt;)&gt;
		&lt;LABEL="<i>table-description</i>"&gt;
		&lt;DATASOURCEOPTIONS=(<i>data-source-options</i>)&gt;
		&lt;PROMOTE | REPLACE&gt;
		&lt;REPEAT&gt;
		 &lt;COPIES=<i>integer</i>&gt;
		&lt;NCHARMULTIPLIER=<i>n</i>&gt;
		&lt;TRANSCODE_FAIL=ERROR | WARN | SILENT &gt;
		&lt;TIMEOUTSECONDS=<i>n</i>&gt;
		&lt;WRITETRANSFERSIZE=<i>integer</i> | <i>integerK</i> | <i>integerM</i> | <i>integerG</i>&gt;;
Form 3: 
LOAD  FILE="<i>path-to-client-file</i>" CASOUT="<i>table-name</i>"

	 &lt;OUTCASLIB="<i>caslib</i>"&gt; 
		&lt;COMPRESS&gt;
		&lt;IMPORTOPTIONS=(FILETYPE=<i>file-type</i> &lt;<i>file-type-options</i>&gt;)&gt;
		&lt;LABEL="<i>table-description</i>"&gt;
		&lt;PROMOTE | REPLACE&gt;
		  &lt;COPIES=<i>integer</i>&gt;
		&lt;TIMEOUTSECONDS=<i>n</i>&gt;
		&lt;WRITETRANSFERSIZE=<i>integer</i> | <i>integerK</i> | <i>integerM</i> | <i>integerG</i>&gt;;
Form 4: 
LOAD  FILE=<i>fileref</i>  CASOUT="<i>table-name</i>"

	 &lt;OUTCASLIB="<i>caslib</i>"&gt; 
		&lt;COMPRESS&gt;
		&lt;IMPORTOPTIONS=(FILETYPE=<i>file-type</i> &lt;<i>file-type-options</i>&gt;)&gt;
		&lt;LABEL="<i>table-description</i>"&gt;
		&lt;PROMOTE | REPLACE&gt;
		  &lt;COPIES=<i>integer</i>&gt;
		&lt;TIMEOUTSECONDS=<i>n</i>&gt;
		&lt;WRITETRANSFERSIZE=<i>integer</i> | <i>integerK</i> | <i>integerM</i> | <i>integerG</i>&gt;;
