PROC CATALOG  CATALOG=&lt;<i>libref</i>.&gt;<i>catalog</i>   &lt;ENTRYTYPE=<i>entry-type</i>&gt; 
	&lt;FORCE&gt; &lt;KILL&gt;;
	CONTENTS  &lt;OUT=<i>SAS-data-set</i>&gt; &lt;FILE=<i>fileref</i>&gt;;  
	COPY  OUT=&lt;<i>libref</i>.&gt;<i>catalog</i> &lt;<i>options</i>&gt;;
		SELECT  <i>entry-1 </i> &lt;<i>entry-2</i> ...&gt; &lt;/ ENTRYTYPE=<i>entry-type</i> &gt;;  
		EXCLUDE  <i>entry-1 </i> &lt;<i>entry-2</i> ...&gt; &lt;/ ENTRYTYPE=<i>entry-type</i>&gt;;  
	CHANGE  <i>old-name-1</i>=<i>new-name-1</i>
		 &lt;<i>old-name-2</i>=<i>new-name-2</i> ...&gt;
		 &lt;/ ENTRYTYPE=<i>entry-type</i>&gt;;  
	EXCHANGE  <i>name-1</i>=<i>other-name-1</i>
		 &lt;<i>name-2</i>=<i>other-name-2</i> ...&gt;
		 &lt;/ ENTRYTYPE=<i>entry-type</i>&gt;;  
	DELETE  <i>entry-1 </i> &lt;<i>entry-2</i> ...&gt;  &lt;/ ENTRYTYPE=<i>entry-type</i>&gt;;  
	MODIFY   <i>entry</i> (DESCRIPTION=&lt;&lt;'&gt;<i>entry-description</i>&lt;'&gt;&gt;) 
		&lt;/ ENTRYTYPE=<i>entry-type</i>&gt;;  
	SAVE  <i>entry-1 </i> &lt;<i>entry-2</i> ...&gt; &lt;/ ENTRYTYPE=<i>entry-type</i> &gt;;
