PROC FCMP  <i>options</i>;
	ABORT ;
	ARRAY  <i>array-name</i>[<i>dimensions</i>] &lt;/NOSYMBOLS&gt; | &lt;<i>variable(s)</i>&gt; | &lt;<i>constant(s)</i>&gt; | &lt;<i>initial-values)</i>&gt;;
	ATTRIB  <i>variable(s)</i> &lt;FORMAT=<i>format-name</i> &gt;&lt;LABEL='<i>label</i>’&gt;&lt; LENGTH=<i>length</i>&gt;;
	DELETEFUNC  <i>function-name</i>;
	DELETESUBR  <i>subroutine-name</i>;
	FUNCTION  <i>function-name(argument(s))</i> &lt;VARARGS&gt; &lt;$&gt; &lt;<i>length</i>&gt; 
		&lt;KIND | GROUP='<i>string</i>' &lt;LABEL=<i>'string-2'</i>&gt;&gt;;
	LABEL  <i>variable</i>=<i>'label'</i>;
	LISTFUNC  <i>function-name</i>;
	LISTSUBR  <i>subroutine-name</i>;
	STRUCT  <i>structure-name</i> <i>variable</i>;
	SUBROUTINE  <i>subroutine-name (argument(s))</i> &lt;VARARGS&gt;
		&lt;LABEL ='<i>label</i>'&gt; &lt;KIND | GROUP='<i>string</i>'&gt;;
	OUTARGS  <i>out-argument(s)</i>;
