PROC OPTNETWORK &lt;DIRECTION=DIRECTED | UNDIRECTED&gt; &lt;INCLUDESELFLINK&gt; &lt;INDEXOFFSET=number&gt; &lt;LINKS=CAS-libref.data-table&gt; &lt;LOGFREQTIME=number | LOGFREQUENCYTIME=number&gt; &lt;LOGLEVEL=NONE | BASIC | MODERATE... &gt; &lt;NODES=CAS-libref.data-table&gt; &lt;NODESSUBSET=CAS-libref.data-table&gt; &lt;NTHREADS=number&gt; &lt;OUTLINKS=CAS-libref.data-table&gt; &lt;OUTNODES=CAS-libref.data-table&gt; &lt;STANDARDIZEDLABELS&gt; &lt;TIMETYPE=CPU | REAL&gt;;     
	BICONNECTEDCOMPONENTS ;

	BY variables;

	CLIQUE &lt;MAXCLIQUES=number | ALL&gt; &lt;MAXTIME=number&gt; &lt;OUT=CAS-libref.data-table&gt; ...;

	CONNECTEDCOMPONENTS &lt;ALGORITHM=&lt;AUTOMATIC | DFS | PARALLEL&gt;... &gt; &lt;INTERNALFORMAT=&lt;FULL | THIN&gt;&gt; ;

	CYCLE &lt;ALGORITHM=&lt;BACKTRACK | BUILD&gt;&gt; &lt;MAXCYCLES=number | ALL&gt; &lt;MAXLENGTH=number&gt; ...;

	DISPLAY &lt;CASESENSITIVE&gt; &lt;EXCLUDE&gt; &lt;EXCLUDEALL&gt; ...;

	DISPLAYOUT &lt;INCLUDEALL&gt; &lt;NOREPLACE&gt; &lt;REPEATED&gt; ...;

	LINEARASSIGNMENT &lt;OUT=CAS-libref.data-table&gt; ;

	LINKSVAR &lt;AUXWEIGHT=column&gt; &lt;FROM=column&gt; &lt;LOWER=column&gt; ...;

	MINCOSTFLOW &lt;INTERNALFORMAT=&lt;FULL | THIN&gt;&gt; &lt;LOGFREQ=number | LOGFREQUENCY=number&gt; &lt;MAXTIME=number&gt; ...;

	MINCUT &lt;MAXCUTS=number&gt; &lt;MAXWEIGHT=number&gt; &lt;OUTCUTSETS=CAS-libref.data-table | OUT=CAS-libref.data-table&gt; ...;

	MINSPANTREE &lt;INTERNALFORMAT=&lt;FULL | THIN&gt;&gt; &lt;OUT=CAS-libref.data-table&gt; ;

	NODESSUBSETVAR &lt;NODE=column&gt; &lt;SINK=column&gt; &lt;SOURCE=column&gt; ...;

	NODESVAR &lt;LOWER=column&gt; &lt;NODE=column&gt; &lt;UPPER=column&gt; ...;

	PATH &lt;INTERNALFORMAT=&lt;FULL | THIN&gt;&gt; &lt;MAXLENGTH=number&gt; &lt;MAXLINKWEIGHT=number&gt; ...;

	SHORTESTPATH &lt;MAXPATHWEIGHT=number&gt; &lt;OUTPATHS=CAS-libref.data-table | OUT=CAS-libref.data-table&gt; &lt;OUTWEIGHTS=CAS-libref.data-table&gt; ...;

	SUMMARY &lt;BICONNECTEDCOMPONENTS&gt; &lt;CONNECTEDCOMPONENTS&gt; &lt;DIAMETERAPPROX=&lt;WEIGHT | UNWEIGHT | BOTH&gt;&gt; ...;

	TRANSITIVECLOSURE &lt;OUT=CAS-libref.data-table&gt; ;

	TSP &lt;ABSOBJGAP=number | ABSOLUTEOBJECTIVEGAP=number&gt; &lt;CUTOFF=number&gt; &lt;CUTSTRATEGY=&lt;AUTOMATIC | NONE | MODERATE&gt;... &gt; ...;
