PROC PRINT  &lt;<i>options</i>&gt;;  
	ATTRIB  <i>variable-list(s) </i> <i>attribute-list(s)</i>;
	BY  &lt;DESCENDING&gt; <i>variable-1</i> &lt;&lt;DESCENDING&gt;<i>variable-2</i> ...&gt; &lt;NOTSORTED&gt;;  
		PAGEBY  <i>BY-variable</i>;
		SUMBY  <i>BY-variable</i>;  
	FORMAT  <i>variable-1</i> &lt; ...<i>variable-n</i>&gt;  &lt;<i>format</i>&gt;  &lt;DEFAULT=<i>default-format</i>&gt;;
	ID  <i>variable(s)</i>
		 &lt;/ STYLE &lt;(<i>location(s)</i>)&gt;=&lt;<i>style-override</i>&gt;&gt;;  
	LABEL  <i>variable-1</i>=<i>label-1</i>&lt; ...<i>variable-n</i>=<i>label-n</i>&gt;;
	SUM  <i>variable(s)</i> 
		&lt;/ STYLE &lt;(<i>location(s)</i>)&gt;=&lt;<i>style-override</i>&gt;&gt;; 
	VAR  <i>variable(s)</i> 
		&lt;/ STYLE &lt;(<i>location(s)</i>)&gt;=&lt;<i>style-override</i>&gt; &gt;;

		WHERE  <i>where-expression-1</i>
			 &lt;<i>logical-operator</i><i> where-expression-n</i>&gt;;
