DEFINE COLUMN  <i>column-path</i> | Base.Template.Column  
	&lt; / STORE=<i>libref</i>.<i>template-store</i>&gt;;
		&lt;<i>column-attribute-1</i>&lt;<i>column-attribute-2</i>&gt; ...&gt;;
	CELLSTYLE  <i>expression-1</i> AS  &lt;<i>style-element-name</i>&gt;&lt;[<i>style-attribute-specification(s)</i>] &gt;
		&lt;, <i>expression-n</i> AS  &lt;<i>style-element-name</i>&gt;&lt;[<i>style-attribute-specification(s)</i>]&gt;&gt;;
	COMPUTE AS  <i>expression</i>;
	DEFINE HEADER  | Base.Template.Header  <i>template-path</i>;  
		<i>statements-and-attributes</i>
		END ;
	DYNAMIC  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;
		&lt;'<i>text-1</i>'&gt; 
		&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;  
	MVAR  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;&lt;'<i>text-1</i>'&gt; 
		&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;  
	NMVAR  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;&lt;'<i>text-1</i>'&gt; 
		&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;  
	NOTES  "<i>text</i>";  
	TRANSLATE  <i>expression-1</i> INTO  <i>expression-2</i>  
		&lt;, <i>expression-n</i> INTO  <i>expression-m</i>&gt;;  
	END ;

DEFINE CROSSTABS  <i>table-path</i> &lt;/ STORE=<i>libref.template-store</i>&gt;;  
	&lt;<i>table-attribute-1</i>; &lt;<i>table-attribute-n</i>&gt;;&gt;  
	CELLVALUE  <i>cellvalues</i>;  
	DEFINE CELLVALUE  <i>cellvalue</i>;  
		<i>statements-and-attributes</i>;  
	END ;  
	DEFINE HEADER  <i>header-name</i>;  
		<i>statements-and-attributes</i>;  
	END ;  
	DEFINE FOOTER  <i>footer-name</i>;  
		<i>statements-and-attributes</i>;  
	END ;  
	DYNAMIC  <i>variable-1</i> &lt;'<i>text-1</i>'&gt; &lt;<i>variable-n</i>&lt;'<i>text-n</i>'&gt;&gt;;  
	FOOTER  <i>footer-name(s)</i>;  
	HEADER <i> header-name(s)</i>;  
	NOTES  <i>text</i>; END ;

DEFINE FOOTER  <i>footer-path</i> | Base.Template.Footer   
	&lt; / STORE=<i>libref</i>.<i>template-store</i>&gt;;
	&lt;<i>header/footer-attribute-1</i>&lt;<i>header/footer-attribute-2</i>&gt; ...&gt;;
	DYNAMIC  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;
		&lt;'<i>text-1</i>'&gt; 
			&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;
	MVAR  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;&lt;'<i>text-1</i>'&gt; 
		&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;
	NMVAR  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;&lt;'<i>text-1</i>'&gt; 
		&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;
	NOTES  "<i>text</i>";
	TEXT  <i>footer-specification</i>;
	TEXT2  <i>footer-specification</i>;
	TEXT3  <i>footer-specification</i>;
	END ;

DEFINE HEADER  <i>header-path</i> | Base.Template.Header  
	&lt;/ STORE=<i>libref</i>.<i>template-store</i>&gt;;
	&lt;<i>header/footer-attribute-1</i>&lt;<i>header/footer-attribute-2</i>&gt; ...&gt;;
	DYNAMIC  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt; 
		&lt;'<i>text-1</i>'&gt;
		&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;
	MVAR  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;&lt;'<i>text-1</i>'&gt; 
		&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;
	NMVAR  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;&lt;'<i>text-1</i>'&gt; 
		&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;
	NOTES  "<i>text</i>";
	TEXT  <i>header-specification</i>;
	TEXT2  <i>header-specification</i>;
	TEXT3  <i>header-specification</i>;
	END ;

DEFINE STYLE  <i>style-path</i> | Base.Template.Style &lt;/ STORE=<i>libref</i>.<i>template-store</i>&gt;;   
	PARENT= <i>style-path</i>;
	NOTES  "<i>text</i>";
	CLASS  <i>style-element-name(s)</i>&lt;"<i>text</i>"&gt;
		 &lt;/ <i>style-attribute-specification(s)</i>&gt;;
	IMPORT  <i>style-specification</i> &lt;<i>media-type-1</i>&lt;<i>, media-type-2</i>&gt; ...&gt;;
	STYLE  <i>style-element-name(s)</i> &lt;FROM <i>style-element-name</i> | _SELF_ &gt; &lt;"<i>text</i>"&gt;
		 &lt;/ <i>style-attribute-specification(s)</i>&gt;;END;

DEFINE TABLE  <i>table-path</i> | Base.Template.Table
	&lt;/ STORE=<i>libref</i>.<i>template-store</i>&gt;;
	&lt;<i>table-attribute-1</i>&lt;<i>table-attribute-2</i>&gt; ...&gt;;
	CELLSTYLE  <i>expression-1</i> AS  &lt;<i>style-element-name</i>&gt;&lt;[<i>style-attribute-specification(s)</i>] &gt;
		&lt;, <i>expression-n</i> AS  &lt;<i>style-element-name</i>&gt;&lt;[<i>style-attribute-specification(s)</i>]&gt;&gt;;
	COLUMN  <i>column(s)</i>;
	DEFINE  <i>template-type</i> <i>template-name</i> &lt;/ <i>options</i>&gt;;  
		<i>statements-and-attributes</i>
		END ;
	DYNAMIC  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;
		&lt;'<i>text-1</i>'&gt; &lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;
	FOOTER  <i>footer-name(s)</i>;
	HEADER  <i>header-name(s)</i>;
	MVAR  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;&lt;'<i>text-1</i>'&gt; 
		&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;
	NMVAR  <i>variable-1</i> &lt;=<i>default-variable-1</i>&gt;&lt;'<i>text-1</i>'&gt; 
		&lt; ...<i>variable-n</i>&lt;=<i>default-variable-n</i>&gt;&lt;'<i>text-n</i>'&gt;&gt;;
	NOTES  "<i>text</i>";
	TRANSLATE  <i>expression-1</i> INTO  <i>expression-2</i>  &lt; , <i>expression-n</i> INTO  <i>expression-m</i>;&gt;
	END ;

DEFINE TAGSET  <i>tagset-path</i> | Base.Template.Tagset 

	&lt;/ STORE=<i>libref</i>.<i>template-store</i> &lt;(READ | WRITE | UPDATE) &gt;&gt;;

		&lt;<i>tagset-attribute-1</i>; &lt;<i>tagset-attribute-n</i>;&gt;&gt;  
	DEFINE EVENT  <i>event-name</i>;<i>statements and attributes</i>  
	NOTES  <i>'text'</i>;  
	END ;

PROC TEMPLATE 

	DEFINE STYLE 
          <i>style-path</i>            | Base.Template.Style 
          &lt;/                 STORE=<i>libref</i>               .<i>template-store</i>
            &gt;           ; 
		PARENT= <i>style-path</i>;

		NOTES                "<i>text</i>";

		CLASS 
            <i>style-element-name(s)</i>
            &lt;"<i>text</i>"&gt;

            &lt;/                   <i>style-attribute-specification(s)</i>&gt;;

		STYLE 
            <i>style-element-name(s)</i>
            &lt;FROM                   <i>style-element-name</i> | _SELF_ &gt;
            &lt;"<i>text</i>"&gt;

            &lt;/                   <i>style-attribute-specification(s)</i>&gt;;

		END ;


	END            ;

	EDIT 
          <i>template-path-1</i>
          &lt;AS                 <i>template-path-2</i>
            &gt;
          &lt; /                 STORE=<i>libref</i>               .<i>template-store</i>
            &gt;            ;             
		<i>statements-and-attributes</i>

		END ;


	DEFINE COLUMN 
          <i>column-path</i>            | Base.Template.Column 

		&lt; /                   STORE=<i>libref</i>.<i>template-store</i>&gt;;

		<i>statements-and-attributes</i>

		END ;


	DEFINE FOOTER 
          <i>footer-path</i>            | Base.Template.Footer 

		&lt; /                   STORE=<i>libref</i>.<i>template-store</i>&gt;;

		<i>statements-and-attributes</i>

		END ;


	DEFINE HEADER 
          <i>template-name</i>            | Base.Template.Header            ;             
		<i>statements-and-attributes</i>

		END ;


	DEFINE TABLE 
          <i>table-path</i>            | Base.Template.Table 

		&lt;/                   STORE=<i>libref</i>.<i>template-store</i>&gt;;

		<i>statements-and-attributes</i>

		END ;


	DEFINE CROSSTABS 
          <i>table-path</i>
          &lt;/                 STORE=<i>libref.template-store</i>
            &gt;           ; 
		&lt;<i>table-attribute-1</i>               ; &lt;<i>table-attribute-n</i>&gt;               ;&gt;

		CELLVALUE 
            <i>cellvalues</i>;

		DEFINE CELLVALUE 
            <i>cellvalue</i>;                 
			<i>statements-and-attributes</i>               ;


		END ;

		DEFINE HEADER 
            <i>header-name</i>;                 
			<i>statements-and-attributes</i>               ;


		END ;

		DEFINE FOOTER 
            <i>footer-name</i>;                 
			<i>statements-and-attributes</i>               ;


		END ;

		DYNAMIC 
            <i>variable-1</i>
            &lt;'<i>text-1</i>'&gt;
            &lt;<i>variable-n</i>&lt;'<i>text-n</i>                   '&gt;&gt;;

		FOOTER 
            <i>footer-name(s)</i>;

		HEADER <i> header-name(s)</i>;

		NOTES 
            <i>text</i>; 


	DEFINE TAGSET 
          <i>tagset-path</i>
          &lt;/                 STORE=<i>libref</i>               .<i>template-store</i>
            &gt;           ; 
		&lt;<i>tagset-attribute-1</i>               ; &lt;...                     <i>tagset-attribute-n</i>                   ;&gt;&gt;

		DEFINE EVENT 
            <i>event-name</i>;                   
			&lt;<i>event-attribute-1</i>;&lt;<i>event-attribute-n</i>                   ;&gt;&gt;


			BLOCK 
              <i>event-name</i>
              &lt; /                     <i>event-statement-condition(s)</i>
                &gt;               ;

			BREAK 
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			CLOSE 
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			CONTINUE 
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			DELSTREAM                 $$<i>stream-variable-name</i>
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			DO 
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			DONE                ;

			ELSE 
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			EVAL                 $&lt;$&gt;
              <i>user-defined-variable</i>
              <i>where-expression</i>
              &lt; / <i>event-statement-condition(s)</i>
                &gt;               ;

			FLUSH 
              &lt;/<i>event-statement-condition(s)</i>
                &gt;               ;

			ITERATE                 $<i>dictionary-variable</i>                | $<i>list-variable</i>
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			NDENT 
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			NEXT                 $<i>dictionary-variable</i>                | $<i>list-variable</i>
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			OPEN                 $$<i>stream-variable-name</i>
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			PUT 
              &lt;<i>function</i>&gt;
              &lt;NL&gt;
              &lt;<i>variable</i>&gt;
              &lt;'<i>text</i>                   ' &gt;
              &lt; /                     <i>event-statement-condition(s)</i>
                &gt;               ;

			PUTL                 (&lt;<i>variable</i>&gt;                | &lt;'<i>text</i>                   ' &gt;               | &lt;<i>function</i>&gt;                | &lt;NL&gt;               ) &lt; /                     <i>event-statement-condition(s)</i>
                &gt;               ;

			PUTLOG                 (&lt;<i>variable</i>&gt;
              &lt;'<i>text</i>                   ' &gt;
              &lt;<i>function</i>&gt;               )&lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;


              PUTQ                 (&lt;<i>variable</i>&gt;                | &lt;'<i>text</i>                   ' &gt;               | &lt;<i>function</i>&gt;                | &lt;NL&gt;               )&lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			PUTSTREAM                 $$<i>stream-variable-name</i>
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;


              PUTVARS 
              <i>variable-group</i>
              <i>variable-group-value</i>
              &lt; /                     <i>event-statement-condition(s)</i>
                &gt;               ;

			SET                 $&lt;$&gt;
              <i>user-defined-variable-name</i>
              <i>user-defined-variable-value</i>

				&lt;/                       <i>event-statement-condition(s)</i>&gt;;


			STOP 
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			TRIGGER 
              <i>event-name</i>
              &lt;START | FINISH &gt;
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			UNBLOCK 
              <i>event-name</i>
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			UNSET 
              ALL                | $<i>memory-variable</i>                | $$<i>stream-variable</i>
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			XDENT 
              &lt;/                     <i>event-statement-condition(s)</i>
                &gt;               ;

			END                ;


		NOTES ;

		END ;


	DELETE 
          <i>item-path</i>
          &lt; /                 STORE=<i>libref</i>               .<i>template-store</i>
            &gt;           ;

	LINK 
          <i>item-path-1</i>
          TO  
          <i>item-path-2</i>
          &lt;/<i>options</i>
            &gt;           ;

	LIST 
          &lt;<i>starting-path</i>&gt;
          &lt;/                 <i>options</i>
            &gt;           ;

	PATH 
          <i>location(s)</i>           ;

	SOURCE 
          <i>item-path</i>
          &lt;/<i>options</i>
            &gt;
          &lt;STORE=<i>libref</i>               .<i>template-store</i>
            &gt;           ;

	TEST             DATA=<i>data-set</i>
          &lt; /                 STORE=<i>libref</i>               .<i>template-store</i>
            &gt;           ;

	END            ;
