PROC TRANSPOSE  &lt;DATA=<i>input-data-set</i>&gt; &lt;DELIMITER=<i>delimiter</i>&gt;  &lt;LABEL=<i>label</i>&gt; 
	&lt;LET&gt; &lt;NAME=<i>name</i>&gt; &lt;OUT=<i>output-data-set</i>&gt; &lt;PREFIX=<i>prefix</i>&gt; &lt;SUFFIX=<i>suffix</i>&gt;;    
	ATTRIB  <i>variable-list(s) </i> <i>attribute-list(s)</i>;  
	BY  &lt;DESCENDING&gt; <i>variable-1</i> 
		 &lt;&lt;DESCENDING&gt;<i>variable-2 </i> ...&gt;
		 &lt;NOTSORTED&gt;;  
	COPY  <i>variable(s)</i>;  
	FORMAT  <i>variable-1</i> &lt; ...<i>variable-n</i>&gt;  &lt;<i>format</i>&gt;  &lt;DEFAULT=<i>default-format</i>&gt;;
	ID  <i>variable</i>;  

		IDLABEL  <i>variable</i>;   
	LABEL  <i>variable-1</i>=<i>label-1</i>&lt; ...<i>variable-n</i>=<i>label-n</i>&gt;;
	VAR  <i>variable(s)</i>; 
	WHERE  <i>where-expression-1</i>
		 &lt;<i>logical-operator</i><i> where-expression-n</i>&gt;;
